import styled from 'styled-components';

export const PostBlock = styled.li`
    cursor: pointer;
    background: palegreen;
    margin: 25px 0;
    padding: 20px;
    width: 28%;
    height: 200px;
    overflow-y: scroll;
    box-shadow: 10px 10px 10px 0px black;
    opacity: 0.7;
    :hover {
        transform: scale(1.1);
        transition: 0.3s;
        opacity: 1;
    }
`;

export const Title = styled.p`
    color: black;
    font-size: 30px;
    font-weight: bold;
    margin: 0 auto;
    text-align: center;
`;

export const Body = styled.p`
    color: brown;
    font-size: 20px;
`;
