import React from 'react';
import Link from 'next/link';
import Post from '../Post/Post';
import * as types from '../../redux/blog/blogTypes';
import { Wrapper, List } from './PostsList.styled';

interface PostsArray {
    posts: types.PostType[];
}

const Posts = ({ posts = [] }: PostsArray): JSX.Element => {
    return (
        <>
            <Wrapper>
                {/* <List>
                    {posts.map((post: types.PostType) => (
                        <Post key={post.id} post={post} />
                    ))}
                </List> */}
                <List>
                    {posts.map((post: types.PostType) => (
                        // <Post key={post.id} post={post} />
                        <>
                            <div>
                                <h3>{post.title}</h3>
                                <p>{post.body}</p>
                            </div>
                            <Link href="/posts/[id]" as={`/posts/${post.id}`}>
                                <a>View</a>
                            </Link>
                            <PostBlock>
                                <Title>{post.title}</Title>
                                <Body>{post.body}</Body>

                                {post.comments &&
                                    (post.comments as Array<types.CommentType>).map((el) => (
                                        <p key={el.id}>
                                            <span>{el.id}: </span> {el.body}
                                        </p>
                                    ))}
                            </PostBlock>
                        </>
                    ))}
                </List>
            </Wrapper>
        </>
    );
};

export default Posts;
