import styled from 'styled-components';

export const PostBlock = styled.li`
    cursor: pointer;
    background: palegreen;
    margin-top: 25px;
    padding: 20px;
    width: 28%;
    height: 200px;
    overflow-y: scroll;
    box-shadow: 9px 10px 5px 0px rgba(57, 82, 63, 0.8);
`;

export const Title = styled.p`
    color: black;
    font-size: 30px;
    font-weight: bold;
    margin: 0 auto;
    text-align: center;
`;

export const Body = styled.p`
    color: darkmagenta;
    font-size: 20px;
`;
