import { connect } from 'react-redux';
import Posts from '../components/PostsList/PostsList';
import * as blogSelectors from '../redux/blog/blogSelectors';
import * as blogOperations from '../redux/blog/blogOperations';

const App = () => {
    <>Hello</>;
};

